CREATE TABLE `user` (  `id` bigint(11) NOT NULL AUTO_INCREMENT,  `account` varchar(50) NOT NULL,  `password` varchar(50) DEFAULT NULL,  `enable` tinyint(1) DEFAULT '1',  `update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  `create_time` timestamp NOT NULL DEFAULT '1970-01-01 08:00:01',  PRIMARY KEY (`id`),  UNIQUE KEY `ACCOUNT_UNK` (`account`)) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;